import"./NavBar-gEiZQ2nI.js";const a="https://newsapi.org/v2/top-headlines?country=gb&category=health&apiKey=2953adaae8b944819cc46a649510105a";function c(){var l=[],o=[];fetch(a).then(t=>{if(!t.ok)throw console.log(t),new Error("Network response was not ok");return t.json()}).then(t=>{var r=t.articles.length;localStorage.setItem("count",r);for(let e=0;e<r;e++)l.push(t.articles[e].title),o.push(t.articles[e].url);localStorage.setItem("liTitle",JSON.stringify(l)),localStorage.setItem("liURL",JSON.stringify(o))}).catch(t=>{console.error("Error:",t)})}document.addEventListener("DOMContentLoaded",function(){c();var l=localStorage.getItem("liTitle"),o=localStorage.getItem("liURL");const t=JSON.parse(l),r=JSON.parse(o);var e=localStorage.getItem("count");for(let i=0;i<e;i++)document.getElementById("articles").innerHTML+=`<div class='articles' onclick='window.location.href="`+r[i]+`"'><br><p>`+t[i]+"</p><br></div><hr>"});
